insert into dealinfo
(dealInfoId, dealNo, dealPerson, dealTime, goodsDesc, enabled)
values

  (#{item.dealinfoid,jdbcType=VARCHAR}, #{item.dealno,jdbcType=VARCHAR}, #{item.dealperson,jdbcType=VARCHAR}, 
    #{item.dealtime,jdbcType=BIGINT}, #{item.goodsdesc,jdbcType=VARCHAR}, #{item.enabled,jdbcType=TINYINT}
    )

mapper.select:
select
from dealinfo where 1=1
and dealInfoId=#{dealinfoid,jdbcType=VARCHAR}

select

from dealinfo
where 1=1

    and dealNo=#{dealNo,jdbcType=VARCHAR}


    and dealPerson=#{dealPerson,jdbcType=VARCHAR}


    and dealTime > #{startTime}  and dealTime < #{endTime}

    and enabled=0 limit  #{startRow} ,#{pageSize}
